images.map:
function(doc) {
  //if (doc.type == 'image' && doc.hasOwnProperty('_attachments')) {
  if (doc.type == 'image') {
    emit([doc.uploadedTs, doc._id, 0], doc._id);
    emit([doc.uploadedTs, doc._id, 1], {_id : doc.userId});
  }
}

images_per_user.map:
function(doc) {
  //if (doc.type == 'image' && doc.hasOwnProperty('_attachments')) {
  if (doc.type == 'image') {
    emit([doc.userId, doc.uploadedTs], doc);
  }
}

images_by_id.map:
function(doc) {
  //if (doc.type == 'image' && doc.hasOwnProperty('_attachments')) {
  if (doc.type == 'image') {
    emit([doc._id, 0], doc._id);
    emit([doc._id, 1], {_id : doc.userId});
  }
}

tags.map:
function(doc) {
  if (doc.type == 'image') {
    var length = doc.tags.length;
    for (var i=0; i<length; i++) {
      emit(doc.tags[i].label, 1);
    }
  }
}

images_by_tags.map:
function(doc) {
  if (doc.type == 'image') {
    var length = doc.tags.length;
    for (var i=0; i<length; i++) {
      emit(doc.tags[i].label, doc);
    }
  }
}

images_by_tags.reduce:
function (keys, values, rereduce) {
  if (rereduce) {
    var result = [ ];
    for (var i=0; i<values.length; i++) {
      var entry = values[i];
      for (var j=0;j<entry.length; j++) {
        var item = entry[j];
        result.push(item);
      }
    }
    return result;
  } else {
    return values;
  }
}
